//     task.short_description = current.short_description;

try {
	var h = new CatalogHelper();
	var sw_id = current.cat_item.correlation_id;
	var user_gr = h.getUserRecord(current.sys_id);
	//todo: UserID Spider field config
	var result;
	var api = new x_bragr_si.RemoteAPIHelper();
	
	var t = api._getToken();
	gs.debug('Global Token: '+t);
	
	
	gs.debug('Email: '+user_gr.email);
	var emp_ID = api.getEmployee(user_gr.email,t);
// 	gs.debug('Email: '+user_gr.mail);
// 	gs.debug('SpiderEmployeeID: '+emp_ID);
	
	//gs.debug('SW_ID: '+sw_id);
	//gs.debug('User_Phone: '+user_gr.phone);
	
	result = api.createSoftwareAssignment(sw_id, emp_ID,t);
	activity.result = result.state;
	gs.debug('Submit Request to Spider Result JSON: '+JSON.stringify(result));
	//activity.result = result.state;
} catch (e) {
	gs.error(e);
} finally {
	activity.result = 'failed';
}

